import { useState, useEffect } from 'react';
import './App.css';
import { Regions, Wine, WineList } from './components';



function App() {
  const [regions, setRegions] = useState([]);
  const [selectedRegion, setSelectedRegion] = useState('');
  const [wines, setWines] = useState([]);
  const [selectedWine, setSelectedWine] = useState('');

  const selectRegion = (region) => {
    setSelectedRegion(region);
  }

  const selectWine = (wine) => {
    setSelectedWine(wine);
  }

  const fetchRegions = async () => {
    const regions = await (await fetch('https://wines-api.herokuapp.com/api/regions')).json();
    setRegions(regions);
  }

  useEffect(() => {
    fetchRegions();

  }, [])

  return (
    <div class="container">
      <h1 class="center-align">Open Wine Database</h1>
      <div class="row">
        <Regions regions={regions} selectRegion={selectRegion} />
        <WineList wines={wines} selectWine={selectWine} />
        <Wine />
      </div>
    </div>

  );
}

export default App;
