import { useState, useEffect } from 'react';
import './App.css';
import { Regions, Wine, WineList } from './components';



function App() {
  const [regions, setRegions] = useState([]);
  const [selectedRegion, setSelectedRegion] = useState('');
  const [wines, setWines] = useState([]);
  const [selectedWine, setSelectedWine] = useState({});

  const onSelectRegion = (region) => {
    setSelectedRegion(region);
  }

  const onSelectWine = (wineId) => {
    fetchWine(wineId);
  }

  const fetchRegions = async () => {
    const regions = await (await fetch('https://wines-api.herokuapp.com/api/regions')).json();
    setRegions(regions);
    fetchWines(regions[0]);  
  }

  const fetchWines = async (region) => {
    const wines = await (await fetch(`https://wines-api.herokuapp.com/api/wines?region=${region}`)).json();
    setWines(wines);
    fetchWine(wines[0].id);  
  }

  const fetchWine = async (id) => {
    const wine = await (await fetch(`https://wines-api.herokuapp.com/api/wines/${id}`)).json();
    setSelectedWine(wine);
  }

  useEffect(() => {
    fetchRegions();
  }, [])

  return (
    <div className="container">
      <h1 className="center-align">Open Wine Database</h1>
      <div className="row">
        <Regions regions={regions} onSelectRegion={onSelectRegion} />
        {wines && <WineList wines={wines} onSelectWine={onSelectWine} />}
        {console.log(selectedWine)}
        {selectedWine.id && <Wine selectedWine={selectedWine}/>}
      </div>
    </div>

  );
}

export default App;
