import { useState, useEffect } from 'react';
import { Regions, Wine, WineList } from './components';
import { useDispatch, useSelector } from 'react-redux';
import { selectWine } from './features/wineSlice';
import { setWines } from './features/winesSlice';
import { setRegions } from './features/regionsSlice';
import { fetchRegions2, fetchWinesFrom } from './api/WineApi';
import './App.css';

function App() {

  const dispatch = useDispatch();
  const wine = useSelector((state) => state.wine.value);
  const wines = useSelector((state) => state.wines.value);
  // const [regions, setRegions] = useState([]);
  // const [wines, setWines] = useState([]);
  // const [selectedWine, setSelectedWine] = useState({});
  const url = 'https://wines-api.herokuapp.com/api/'

  const fetchRegions = async () => {
    const regions = await fetchRegions2();
    // const regions = await (await fetch(`${url}regions`)).json();
    dispatch(setRegions(regions))
    // setRegions(regions);
    fetchWines(regions[0]);  
  }

  const fetchWines = async (region) => {
    const wines = await fetchWinesFrom(region);
    dispatch(setWines(wines));
    setWines(wines);
    wines.length ? fetchWine(wines[0].id) : dispatch(selectWine({})); //setSelectedWine({});
  }

  const fetchWine = async (id) => {
    const wine = await (await fetch(`${url}wines/${id}`)).json();
    dispatch(selectWine(wine)); //setSelectedWine(wine);
  }

  const onSelectRegion = (region) => {
    fetchWines(region);   
  }

  const onSelectWine = (wineId) => {
    fetchWine(wineId);
  }

  useEffect(() => {
    fetchRegions();
  }, [])

  return (
    <div className="container">
      <h1 className="center-align">Open Wine Database</h1>
      <div className="row">
        <Regions onSelectRegion={onSelectRegion} />
        {wines && <WineList onSelectWine={onSelectWine} />}
        {/* {console.log(selectedWine)} */}
        {/* {selectedWine.id && <Wine wine={selectedWine}/>} */}
        {wine.id && <Wine />}
      </div>
    </div>

  );
}

export default App;
